<!-- Main -->
<section id="main" class="wrapper">
    <div class="container">

        <header class="major special">
            <h2>@Model.Value("articlesTitle")</h2>
        </header>

        <a href="#" class="image fit"><img src="images/pic10.jpg" alt="" /></a>
        @Model.Value("articlesBodyText")
    </div>
</section>
@{
	var selection = Umbraco.Content(Guid.Parse("3e5ad438-e0b2-489b-afa9-3a4f5fb9965b"))
    .Children("articlesItem")
    .Where(x => x.IsVisible())
    .OrderByDescending(x => x.CreateDate);
}
<ul>
	@foreach (var item in selection)
	{
        <div class="article">
            <div class="articletitle"><a href="@item.Url">@item.Name</a></div> BRAVO
            <div class="articlepreview">@item.Value("articleContent").ToString().Truncate(100) <a href="@item.Url"> <br />  Read More..</a></div>
        </div>
        <hr/>
	}
</ul>

